package halloqueensgambit.java;

import java.io.IOException;
import java.util.Scanner;


public class App {
    public String getGreeting() {
        return "a";
    }

    public void printNextGames(Game game) {
        var lst = game.allNextGames();
        System.out.println("ALL NEXT GAME:");
        for (var g : lst) {
            System.out.println();
            System.out.println(g);
        }
    }

    public void twoPlayer(Game game) {
        Game newGame = game;
        // TODO: this is lazy
        while (true) {
            System.out.println(newGame);
            newGame = IO.playerMove(newGame, new Scanner(System.in));
        }
    }

    public static void main(String[] args) throws IOException {
        String fileName;
        if (args.length == 0) {
            fileName = "/bestmove/a.txt";
        } else {
            fileName = args[0];
        }
        Game game = IO.readGameFromFile(fileName);
        System.out.println(game);

        FogartySolver.moveRating bestMove_d = FogartySolver.monteCarlo(game, 0, 3, 4);
        System.out.println("Best move: " + bestMove_d + ", positions seen: " + FogartySolver.positionsSeen);
        FogartySolver.positionsSeen = 0;
        System.out.println(game.makeMove(bestMove_d.m()));

    }

    /* IO TO SCAN GAME FROM TXT FILES */
}
